INSERT INTO berita (id, foto, judul, isi, tanggal_terbit, penulis, status) VALUES
(1, '1742707000001.png', 'Gotong Royong Pembersihan Lingkungan Desa Bahontobungku', 'Warga Desa Bahontobungku melaksanakan kegiatan gotong royong membersihkan lingkungan desa, fokus pada drainase dan area publik. Kegiatan ini bertujuan untuk menjaga kebersihan dan mencegah banjir, terutama menjelang musim hujan. Partisipasi aktif dari seluruh elemen masyarakat menunjukkan semangat kebersamaan yang tinggi.', '2025-07-15', 'Admin Desa', 'Dipublikasi'),
(2, '1742707000002.png', 'Pelatihan Budidaya Rumput Laut untuk Peningkatan Ekonomi Warga', 'Dinas Kelautan dan Perikanan Morowali mengadakan pelatihan budidaya rumput laut bagi masyarakat pesisir Bahontobungku. Program ini diharapkan dapat membuka peluang ekonomi baru dan meningkatkan pendapatan keluarga, mengingat potensi kelautan yang melimpah di wilayah ini.', '2025-07-14', 'Pemerintah Daerah', 'Dipublikasi'),
(3, '1742707000003.png', 'Peresmian Balai Pertemuan Desa Bahontobungku', 'Balai Pertemuan Desa Bahontobungku secara resmi diresmikan oleh Bupati Morowali. Fasilitas ini diharapkan dapat menjadi pusat kegiatan sosial, budaya, dan musyawarah warga, serta mendukung berbagai program pembangunan desa.', '2025-07-13', 'Kepala Desa', 'Dipublikasi'),
(4, '1742707000004.png', 'Penyuluhan Kesehatan Pencegahan Stunting di Posyandu', 'Puskesmas Bahontobungku mengadakan penyuluhan kesehatan tentang pencegahan stunting pada balita. Kegiatan ini melibatkan ibu-ibu dan kader posyandu untuk meningkatkan kesadaran akan pentingnya gizi seimbang sejak dini.', '2025-07-12', 'Tim Kesehatan Desa', 'Dipublikasi'),
(5, '1742707000005.png', 'Turnamen Bola Voli Antar Dusun Meriahkan HUT RI ke-80', 'Dalam rangka memeriahkan Hari Ulang Tahun Kemerdekaan Republik Indonesia ke-80, Desa Bahontobungku menyelenggarakan turnamen bola voli antar dusun. Acara ini tidak hanya sebagai ajang kompetisi, tetapi juga mempererat tali silaturahmi antar warga.', '2025-07-11', 'Panitia HUT RI', 'Dipublikasi'),
(6, '1742707000006.png', 'Program Penanaman Mangrove untuk Konservasi Pesisir', 'Komunitas lingkungan bersama warga Bahontobungku memulai program penanaman bibit mangrove di area pesisir. Inisiatif ini bertujuan untuk mencegah abrasi, menjaga ekosistem laut, dan mendukung keberlanjutan lingkungan.', '2025-07-10', 'Komunitas Peduli Lingkungan', 'Dipublikasi'),
(7, '1742707000007.png', 'Sosialisasi Program Bantuan Sosial Tunai (BST) Tahap II', 'Pemerintah Desa Bahontobungku mengadakan sosialisasi dan pendistribusian Bantuan Sosial Tunai (BST) Tahap II kepada keluarga penerima manfaat. Diharapkan bantuan ini dapat meringankan beban ekonomi masyarakat di tengah kondisi saat ini.', '2025-07-09', 'Pemerintah Desa', 'Dipublikasi'),
(8, '1742707000008.png', 'Pengembangan Potensi Wisata Air Terjun Desa Bahontobungku', 'Pemerintah desa dan kelompok sadar wisata sedang mengkaji potensi pengembangan Air Terjun Bahontobungku sebagai destinasi wisata unggulan. Rencana ini mencakup perbaikan akses jalan dan fasilitas pendukung.', '2025-07-08', 'Pokdarwis Desa', 'Draft'),
(9, '1742707000009.png', 'Peningkatan Kualitas Pendidikan Melalui Program Belajar Tambahan', 'Relawan pendidikan di Bahontobungku memulai program belajar tambahan untuk siswa sekolah dasar. Fokus utama pada mata pelajaran dasar seperti matematika dan bahasa Indonesia untuk meningkatkan prestasi akademik anak-anak desa.', '2025-07-07', 'Relawan Pendidikan', 'Dipublikasi'),
(10, '1742707000010.png', 'Musyawarah Desa Rencana Pembangunan Tahun Anggaran 2026', 'Pemerintah Desa Bahontobungku mengadakan musyawarah desa untuk menyusun rencana pembangunan tahun anggaran 2026. Seluruh elemen masyarakat diundang untuk memberikan masukan dan prioritas pembangunan.', '2025-07-06', 'Pemerintah Desa', 'Dipublikasi'),
(11, '1742707000011.png', 'Normalisasi Sungai untuk Pencegahan Banjir', 'Proyek normalisasi sungai di Desa Bahontobungku dimulai untuk mengatasi masalah banjir yang sering terjadi saat musim hujan. Proyek ini melibatkan pengerukan sedimen dan pelebaran bantaran sungai.', '2025-07-05', 'Dinas Pekerjaan Umum', 'Dipublikasi'),
(12, '1742707000012.png', 'Gerakan Penanaman Pohon Buah di Lahan Kosong Desa', 'Warga desa berpartisipasi dalam gerakan penanaman pohon buah di lahan-lahan kosong. Inisiatif ini bertujuan untuk meningkatkan ketahanan pangan lokal dan penghijauan desa.', '2025-07-04', 'Kelompok Tani Desa', 'Dipublikasi'),
(13, '1742707000013.png', 'Penyuluhan Hukum dan Kesadaran Pajak untuk Warga', 'Kantor Pajak Pratama Morowali bekerja sama dengan Pemerintah Desa Bahontobungku mengadakan penyuluhan hukum dan kesadaran pajak bagi warga. Materi meliputi hak dan kewajiban sebagai wajib pajak.', '2025-07-03', 'Kantor Pajak Pratama', 'Dipublikasi'),
(14, '1742707000014.png', 'Perayaan Hari Anak Nasional dengan Berbagai Lomba', 'Desa Bahontobungku merayakan Hari Anak Nasional dengan mengadakan berbagai lomba kreatif dan edukatif untuk anak-anak. Acara ini bertujuan untuk menumbuhkan semangat kebersamaan dan kreativitas.', '2025-07-02', 'PKK Desa', 'Dipublikasi'),
(15, '1742707000015.png', 'Program Pelatihan Pengelolaan Sampah Berbasis Komunitas', 'Dinas Lingkungan Hidup mengadakan pelatihan pengelolaan sampah berbasis komunitas di Desa Bahontobungku. Peserta diajarkan cara memilah dan mendaur ulang sampah rumah tangga.', '2025-07-01', 'Dinas Lingkungan Hidup', 'Dipublikasi'),
(16, '1742707100016.png', 'Pembangunan Jaringan Irigasi Tersier untuk Pertanian', 'Proyek pembangunan jaringan irigasi tersier sedang berlangsung di area persawahan desa. Diharapkan dapat meningkatkan produktivitas pertanian dan efisiensi penggunaan air.', '2025-06-30', 'Dinas Pertanian', 'Dipublikasi'),
(17, '1742707100017.png', 'Sosialisasi Bahaya Narkoba dan Kenakalan Remaja', 'Badan Narkotika Nasional (BNN) bekerja sama dengan Pemerintah Desa mengadakan sosialisasi bahaya narkoba dan kenakalan remaja bagi pemuda desa. Pentingnya peran keluarga dalam pengawasan juga ditekankan.', '2025-06-29', 'BNN Morowali', 'Dipublikasi'),
(18, '1742707100018.png', 'Peningkatan Kapasitas BUMDes Melalui Pelatihan Manajemen', 'Pengurus Badan Usaha Milik Desa (BUMDes) Bahontobungku mengikuti pelatihan manajemen dan pengembangan usaha. Tujuannya untuk meningkatkan kinerja BUMDes dalam mengelola potensi ekonomi desa.', '2025-06-28', 'Dinas Koperasi dan UKM', 'Dipublikasi'),
(19, '1742707100019.png', 'Workshop Kerajinan Tangan dari Bahan Lokal', 'Kelompok ibu-ibu PKK Desa Bahontobungku mengikuti workshop kerajinan tangan menggunakan bahan-bahan lokal seperti tempurung kelapa dan eceng gondok. Hasil karya diharapkan dapat menjadi produk unggulan desa.', '2025-06-27', 'Dekranasda Kab. Morowali', 'Dipublikasi'),
(20, '1742707100020.png', 'Pengadaan Lampu Penerangan Jalan Umum di Beberapa Titik', 'Pemerintah desa melakukan pengadaan dan pemasangan lampu penerangan jalan umum di beberapa titik strategis. Diharapkan dapat meningkatkan keamanan dan kenyamanan warga di malam hari.', '2025-06-26', 'Pemerintah Desa', 'Dipublikasi');
