INSERT INTO pengumuman (id, judul, isi, tanggal_mulai, tanggal_selesai) VALUES
(1, 'Jadwal Musyawarah Desa Tahunan', 'Diberitahukan kepada seluruh warga Desa Bahontobungku, akan diadakan Musyawarah Desa Tahunan pada tanggal 25 Juli 2025 di Balai Desa. Diharapkan kehadiran bapak/ibu untuk membahas rencana pembangunan desa.', '2025-07-10', '2025-07-25'),
(2, 'Program Vaksinasi Massal COVID-19', 'Pemerintah Desa Bahontobungku bekerja sama dengan Puskesmas akan mengadakan program vaksinasi massal COVID-19 dosis booster pada tanggal 20 Juli 2025 di Balai Desa. Mohon membawa KTP.', '2025-07-05', '2025-07-20'),
(3, 'Pendaftaran Bantuan Bibit Unggul Pertanian', 'Bagi petani di Desa Bahontobungku yang berminat mendapatkan bantuan bibit unggul, pendaftaran dibuka mulai tanggal 18 Juli hingga 30 Juli 2025 di Kantor Desa.', '2025-07-18', '2025-07-30'),
(4, 'Lomba Kebersihan Lingkungan Antar Dusun', 'Dalam rangka memperingati Hari Kemerdekaan RI, akan diadakan lomba kebersihan lingkungan antar dusun. Penilaian akan dilaksanakan pada tanggal 10 Agustus 2025. Mari jaga kebersihan desa kita!', '2025-07-01', '2025-08-10'),
(5, 'Pengaktifan Kembali Posyandu Balita dan Lansia', 'Posyandu Balita dan Lansia Desa Bahontobungku akan aktif kembali setiap hari Selasa minggu pertama dan ketiga setiap bulan, dimulai tanggal 2 Juli 2025. Mohon partisipasi ibu-ibu dan lansia.', '2025-06-25', '2025-12-31'),
(6, 'Sosialisasi Program Keluarga Harapan (PKH) Baru', 'Akan dilaksanakan sosialisasi program PKH bagi calon penerima manfaat baru pada tanggal 22 Juli 2025 di Balai Desa. Diharapkan kehadiran perwakilan keluarga yang telah didata.', '2025-07-15', '2025-07-22'),
(7, 'Jadwal Ronda Malam Lingkungan', 'Kepada seluruh kepala keluarga, dimohon untuk memperhatikan jadwal ronda malam lingkungan yang telah disepakati. Keamanan desa adalah tanggung jawab bersama. Jadwal terbaru dapat dilihat di papan pengumuman desa.', '2025-07-01', '2025-12-31'),
(8, 'Program Pelatihan Keterampilan Digital untuk Pemuda', 'Pemerintah desa membuka pendaftaran untuk program pelatihan keterampilan digital (desain grafis dan pemasaran online) bagi pemuda/i usia 18-25 tahun. Pendaftaran hingga 5 Agustus 2025.', '2025-07-20', '2025-08-05'),
(9, 'Imbauan Waspada Kebakaran Hutan dan Lahan', 'Mengingat musim kemarau, diimbau kepada seluruh warga untuk meningkatkan kewaspadaan terhadap potensi kebakaran hutan dan lahan. Hindari pembakaran sampah sembarangan.', '2025-07-01', '2025-10-31'),
(10, 'Penerimaan Peserta Didik Baru PAUD Bahontobungku', 'Pendaftaran Peserta Didik Baru PAUD Desa Bahontobungku Tahun Ajaran 2025/2026 dibuka mulai tanggal 1 Agustus hingga 31 Agustus 2025 di gedung PAUD.', '2025-08-01', '2025-08-31'),
(11, 'Pemberitahuan Pemadaman Listrik Terjadwal', 'Sehubungan dengan pemeliharaan jaringan, akan ada pemadaman listrik terjadwal di sebagian wilayah desa pada tanggal 26 Juli 2025, pukul 09.00-12.00 WITA. Mohon maaf atas ketidaknyamanan.', '2025-07-20', '2025-07-26'),
(12, 'Pengambilan Bantuan Langsung Tunai (BLT) Dana Desa', 'Bagi penerima BLT Dana Desa, pengambilan bantuan dapat dilakukan di Balai Desa pada tanggal 28-30 Juli 2025, pukul 09.00-15.00 WITA. Harap membawa KTP dan KK asli.', '2025-07-25', '2025-07-30'),
(13, 'Jadwal Kebersihan Lingkungan Rutin', 'Diingatkan kembali kepada seluruh warga untuk melaksanakan kegiatan kebersihan lingkungan rutin setiap hari Minggu pagi di lingkungan masing-masing. Mari jaga kebersihan desa kita.', '2025-07-01', '2025-12-31'),
(14, 'Pendaftaran Calon Anggota Karang Taruna', 'Karang Taruna Desa Bahontobungku membuka pendaftaran anggota baru bagi pemuda/i usia 16-25 tahun. Pendaftaran dibuka hingga 15 Agustus 2025 di Sekretariat Karang Taruna.', '2025-07-25', '2025-08-15'),
(15, 'Sosialisasi Program Ketahanan Pangan Desa', 'Akan diadakan sosialisasi program ketahanan pangan desa, termasuk budidaya sayuran hidroponik dan ternak lele, pada tanggal 1 Agustus 2025 di Balai Desa. Terbuka untuk umum.', '2025-07-28', '2025-08-01'),
(16, 'Imbauan Penggunaan Air Bersih Secara Hemat', 'Mengingat musim kemarau, diimbau kepada seluruh warga untuk menggunakan air bersih secara hemat dan bijak. Laporkan jika ada kebocoran pipa air.', '2025-07-20', '2025-10-31'),
(17, 'Pelatihan Peningkatan Kapasitas Aparatur Desa', 'Aparatur Desa Bahontobungku akan mengikuti pelatihan peningkatan kapasitas di Kantor Camat pada tanggal 3-5 Agustus 2025. Pelayanan kantor desa mungkin sedikit terganggu.', '2025-08-01', '2025-08-05'),
(18, 'Pengumuman Hasil Seleksi Penerima Bantuan Usaha Kecil', 'Hasil seleksi penerima bantuan usaha kecil dari pemerintah desa akan diumumkan pada tanggal 7 Agustus 2025 di papan pengumuman desa dan website resmi desa.', '2025-08-05', '2025-08-07'),
(19, 'Jadwal Posyandu Keliling Dusun', 'Untuk menjangkau seluruh warga, Posyandu akan mengadakan layanan keliling di setiap dusun. Jadwal lengkap dapat dilihat di papan pengumuman masing-masing dusun.', '2025-08-01', '2025-12-31'),
(20, 'Peringatan Hari Raya Idul Adha 1447 H', 'Pemerintah Desa Bahontobungku mengucapkan Selamat Hari Raya Idul Adha 1447 H. Mari tingkatkan semangat berkurban dan kebersamaan. Pelaksanaan sholat Idul Adha akan diumumkan lebih lanjut.', '2025-06-10', '2025-06-17'); -- Tanggal ini disesuaikan dengan perkiraan Idul Adha 2026
